    static String example = "data:image/png;base64,";

    //byte数组到图片
    public static void byteToImage(byte[] data, String path) {
        if (data.length < 3 || path.equals("")) return;
        try {
            File file = new File(path);
            if (file.exists())
                file.delete();
            FileImageOutputStream imageOutput = new FileImageOutputStream(file);
            imageOutput.write(data, 0, data.length);
            imageOutput.flush();
            imageOutput.close();
        } catch (Exception ex) {
            System.out.println("Exception: " + ex);
            ex.printStackTrace();
        }
    }

    //byte数组到base64字符串
    public static String byteToBase64(byte[] data) {
        return Base64.getEncoder().encodeToString(data);
    }

    //base64字符串到byte数组
    public static byte[] base64ToByte(String data) {
        return Base64.getDecoder().decode(removeBase64Header(data));
    }

    //图片到byte数组
    public static byte[] imageToByte(String path) {
        byte[] data = null;
        FileImageInputStream input = null;
        try {
            input = new FileImageInputStream(new File(path));
            ByteArrayOutputStream output = new ByteArrayOutputStream();
            byte[] buf = new byte[1024];
            int numBytesRead = 0;
            while ((numBytesRead = input.read(buf)) != -1) {
                output.write(buf, 0, numBytesRead);
            }
            data = output.toByteArray();
            output.close();
            input.close();
        } catch (IOException ex) {
            ex.printStackTrace();
        }
        return data;
    }

    // base64添加png解析标记
    public static String addBase64PngHeader(String base64) {
        return "data:image/png;base64," + base64;
    }

    // base64移除解析标记
    public static String removeBase64Header(String base64) {
        int index = base64.indexOf("base64,");
        return index == -1 ? base64 : base64.substring(index + 7);
    }

    // Png类图片
    public static class Png {
        byte[] header = new byte[8];
        ArrayList<Chunk> chunks = new ArrayList<>();

        // 从某路径读取图片
        public static Png readFrom(String path) {
            byte[] original = imageToByte(path);
            return incise(original);
        }

        // 切割数据块
        public static Png incise(byte[] data) {
            if (data.length < 8) return null;

            Png p = new Png();

            System.arraycopy(data, 0, p.header, 0, 8);
            for (int i = 8; i < data.length - 1; ) {
                Chunk c = new Chunk();
                c.length = Arrays.copyOfRange(data, i, i + 4);
                c.type = Arrays.copyOfRange(data, i + 4, i + 8);
                int length = c.getLength();
                System.out.println(length);
                c.data = Arrays.copyOfRange(data, i + 8, i + 8 + length);
                c.crc = Arrays.copyOfRange(data, i + 8 + length, i + 8 + length + 4);
                p.chunks.add(c);
                i += 8 + length + 4;
            }

            return p;
        }

        // 清除并填入自定义信息
        public String setIEXT(String text) {
            byte[] data = text.getBytes(StandardCharsets.UTF_8);

            clearIEXT();

            Chunk IEXT = new Chunk();
            IEXT.setLength(data.length);
            IEXT.setType("IEXT");
            IEXT.setData(data);
            IEXT.setCrc("0000");
            this.chunks.add(IEXT);
            return text;
        }

        // 清除自定义信息
        public void clearIEXT() {
            chunks.removeIf(c -> c.getType().equals("IEXT"));
        }

        // 读取自定义信息
        public String readIEXT() {
            StringBuilder res = new StringBuilder();
            chunks.forEach(chunk ->
                    res.append(chunk.getType().equals("IEXT") ? new String(chunk.data, StandardCharsets.UTF_8) : ""));
            return res.toString();
        }

        // 整合数据块
        public byte[] getBytes() {
            ArrayList<byte[]> ab = new ArrayList<>();

            ab.add(header);
            for (Chunk c : chunks) {
                ab.add(c.getBytes());
            }

            return MyUtils.byteMergerAll(ab.toArray(new byte[ab.size()][]));
        }

        // 获取base64编码
        public String getBase64() {
            return byteToBase64(getBytes());
        }

        // 数据块类
        public static class Chunk {
            byte[] length; // 4字节 指定数据块中数据域的长度，其长度不超过(231－1)字节
            byte[] type; // 4字节 数据块类型码由ASCII字母(A-Z和a-z)组成
            byte[] data; // 可变长度 存储按照Chunk Type Code指定的数据
            byte[] crc; // 4字节 存储用来检测是否有错误的循环冗余码

            public void setLength(int n) {
                byte[] b = new byte[4];
                b[3] = (byte) (n & 0xff);
                b[2] = (byte) (n >> 8 & 0xff);
                b[1] = (byte) (n >> 16 & 0xff);
                b[0] = (byte) (n >> 24 & 0xff);
                this.length = b;
            }

            public int getLength() {
                return (int) (length[0] * Math.pow(2, 24) + length[1] * Math.pow(2, 16) + length[2] * Math.pow(2, 8) + length[3]);
            }

            public void setType(String type) {
                byte[] t = type.getBytes(StandardCharsets.UTF_8);
                byte[] b = new byte[4];
                System.arraycopy(t, 0, b, 0, t.length);
                this.type = b;
            }

            public String getType() {
                return new String(type);
            }

            public void setData(byte[] data) {
                this.data = data;
            }

            public void setCrc(String crc) {
                byte[] c = crc.getBytes(StandardCharsets.UTF_8);
                byte[] b = new byte[4];
                System.arraycopy(c, 0, b, 0, c.length);
                this.crc = b;
            }

            public byte[] getBytes() {
                int dataLen = this.data.length;
                byte[] b = new byte[12 + dataLen];
                System.arraycopy(this.length, 0, b, 0, 4);
                System.arraycopy(this.type, 0, b, 4, 4);
                System.arraycopy(this.data, 0, b, 8, dataLen);
                System.arraycopy(this.crc, 0, b, 8 + dataLen, 4);
                return b;
            }

            @Override
            public String toString() {
                return "{" +
                        "\n  length:" + getLength() +
                        ",\n  type:" + getType() +
                        ",\n  data:" + Arrays.toString(data) +
                        ",\n  crc:" + Arrays.toString(crc) +
                        "\n}";
            }
        }
    }

